<template>
  <div class="f-field">
    <div v-if="label" class="label">
      {{ label }}
    </div>
    <span v-if="hasRequired" class="required">必須</span>
    <span v-if="message" class="message">{{ message }}</span>
    <div class="content">
      <input
        :value="value"
        :type="type"
        class="input"
        :placeholder="placeholder"
        :maxlength="maxlength"
        @paste="$emit('paste', $event)"
        @input="$emit('input', $event.target.value)"
      />
      <div v-if="error" class="note">{{ error }}</div>
    </div>
  </div>
</template>

<script>
import { v, validationErrors } from '~/plugins/vuelidate.client'

export default {
  props: {
    label: {
      type: String,
      default: '',
    },
    type: {
      type: String,
      default: 'text',
    },
    value: {
      type: String,
      required: true,
    },
    message: {
      type: String,
      default: '',
    },
    placeholder: {
      type: String,
      default: '',
    },
  },
  computed: {
    v,
    validationErrors,
    hasRequired() {
      return this.v?.$params.required?.type === 'required'
    },
    error() {
      return this.validationErrors?.length > 0 ? this.validationErrors[0] : null
    },
    maxlength() {
      return this.v?.$params.maxLength?.max
    },
  },
}
</script>

<style lang="scss" scoped>
.f-field {
