    border: 1px solid red;
    font-size: 0.75rem;
    font-weight: normal;
  }
  > .message {
    color: #888;
    font-size: 0.75rem;
  }
  > .content {
    position: relative;
  }
  > .content > .note {
    display: none;
    position: absolute;
    top: -26px;
    right: 0;
    padding: 2px 4px;
    border-radius: 3px;
    background: red;
    color: #fff;
    font-size: 0.75rem;
    &::after {
      position: absolute;
      right: 16px;
      bottom: -6px;
      content: '';
      width: 0;
      height: 0;
      border-style: solid;
      border-width: 8px 8px 0 8px;
      border-color: red transparent transparent transparent;
    }
  }
  > .content > .input {
    width: 100%;
    height: 52px;
    padding: 0 16px;
    background: #f6f6f6;
    border: 1px solid #ccc;
    border-radius: 26px;
    outline: none;
    background-position: right 12px center;
    background-size: 24px;
  }
  &.-v-correct > .content > .input {
    background-image: url(data:image/svg+xml;base64,);
    background-repeat: no-repeat;
  }
  &.-v-error > .content > .input {
    background-image: url(data:image/svg+xml;base64,);
    background-repeat: no-repeat;
    border: 1.4px solid red;
    &:focus + .note {
      display: block;
    }
  }
}
</style>
